macro.dbt_artifacts.upload_results.macro_sql:
{% macro upload_results(results) -%}

    {% if execute %}

        {% set datasets_to_load = ['exposures', 'seeds', 'snapshots', 'invocations', 'sources', 'tests', 'models'] %}
        {% if results != [] %}
            {# When executing, and results are available, then upload the results #}
            {% set datasets_to_load = ['model_executions', 'seed_executions', 'test_executions', 'snapshot_executions'] + datasets_to_load %}
        {% endif %}

        {# Upload each data set in turn #}
        {% for dataset in datasets_to_load %}

            {% do log("Uploading " ~ dataset.replace("_", " "), true) %}

            {# Get the results that need to be uploaded #}
            {% set objects = dbt_artifacts.get_dataset_content(dataset) %}

            {# Upload in chunks to reduce the query size #}
            {% if dataset == 'models' %}
                {% set upload_limit = 50 if target.type == 'bigquery' else 100 %}
            {% else %}
                {% set upload_limit = 300 if target.type == 'bigquery' else 5000 %}
            {% endif %}

            {# Loop through each chunk in turn #}
            {% for i in range(0, objects | length, upload_limit) -%}

                {# Get just the objects to load on this loop #}
                {% set content = dbt_artifacts.get_table_content_values(dataset, objects[i: i + upload_limit]) %}

                {# Insert the content into the metadata table #}
                {{ dbt_artifacts.insert_into_metadata_table(
                    dataset=dataset,
                    fields=dbt_artifacts.get_column_name_list(dataset),
                    content=content
                    )
                }}

            {# Loop the next 'chunk' #}
            {% endfor %}

        {# Loop the next 'dataset' #}
        {% endfor %}

    {% endif %}

{%- endmacro %}

macro.dbt_artifacts.get_table_content_va.macro_sql:
{% macro get_table_content_values(dataset, objects_to_upload) %}

    {# Convert the results to data to be imported #}

    {% if dataset == 'model_executions' %}
        {% set content = dbt_artifacts.upload_model_executions(objects_to_upload) %}
    {% elif dataset == 'seed_executions' %}
        {% set content = dbt_artifacts.upload_seed_executions(objects_to_upload) %}
    {% elif dataset == 'test_executions' %}
        {% set content = dbt_artifacts.upload_test_executions(objects_to_upload) %}
    {% elif dataset == 'snapshot_executions' %}
        {% set content = dbt_artifacts.upload_snapshot_executions(objects_to_upload) %}
    {% elif dataset == 'exposures' %}
        {% set content = dbt_artifacts.upload_exposures(objects_to_upload) %}
    {% elif dataset == 'models' %}
        {% set content = dbt_artifacts.upload_models(objects_to_upload) %}
    {% elif dataset == 'seeds' %}
        {% set content = dbt_artifacts.upload_seeds(objects_to_upload) %}
    {% elif dataset == 'snapshots' %}
        {% set content = dbt_artifacts.upload_snapshots(objects_to_upload) %}
    {% elif dataset == 'sources' %}
        {% set content = dbt_artifacts.upload_sources(objects_to_upload) %}
    {% elif dataset == 'tests' %}
        {% set content = dbt_artifacts.upload_tests(objects_to_upload) %}
    {# Invocations only requires data from variables available in the macro #}
    {% elif dataset == 'invocations' %}
        {% set content = dbt_artifacts.upload_invocations() %}
    {% endif %}

    {{ return(content) }}

{% endmacro %}

